/*
 * elasticsearchmigration.js: Top-level include defining esm
 *
 *
 */

var esm = exports;

esm.version = '1.0.0';

esm.mapping = require('./elasticsearchmigration/_mapping/mapping');
esm.river = require('./elasticsearchmigration/_river/river');
esm.core = require('./elasticsearchmigration/core/core');
esm.template = require('./elasticsearchmigration/_template/template');
esm.options = {};
esm.loadOptions = function(argv) {
    argv.forEach(function (val, i, array) {
        switch(i) {
            case 1 :
                esm.options.log = val + ".log";
                break;
            case 2 :
                esm.options.secure = (val === 'https://');
                break;
            case 3:
                esm.options.host = val;
                break;
            case 4:
                esm.options.port = parseInt(val);
                break;
            case 5:
                esm.options.river_url = val;
                break;
            case 6:
                esm.options.river_user = val;
                break;
            case 7:
                esm.options.river_password = val;
                break;
        }

    });
};